import React, { Component } from "react";
import './Pay'
export default class Pay extends Component {
	render() {
		const {cartItem} = this.props;
		return (
			<div>
				{cartItem.length === 0 ? (
					<div>
						<h1>cart is empty</h1>
					</div>
				) : (
					<div>
						<h1>cart have: {cartItem.length} item</h1>
					</div>
				)}
				<div>
					{cartItem.map((item) => (
						<div className='pay__data'>
							<div>
								<h3>{item.name}</h3>
								<img src={item.image} alt="" width="100px" />
								<div>
									<p>{item.type}</p>
									<p>
										{item.rating}X{item.count}
									</p>
								</div>
							</div>
							<button onClick={()=>this.props.remove(item)}>remove</button>
						</div>
					))}
				</div>
                <div>
                    <h3>
                       Total:
                    </h3>
                    <h3>{cartItem.reduce((a,c)=>a+c.rating*c.count,0)}</h3>
                    <button> procced</button>
                </div>
			</div>
		);
	}
}
